class Firma {    String isim;    double isletmeSermayesi;    double toplamVarliklar;    double dagitilmayanKarlar;    double faizVeVergiOncesiKar;    double ozkaynak;    double borclar;    double satislar;    public Firma(String isim, double isletmeSermayesi, double toplamVarliklar,                 double dagitilmayanKarlar, double faizVeVergiOncesiKar,                 double ozkaynak, double borclar, double satislar) {        this.isim = isim;        this.isletmeSermayesi = isletmeSermayesi;        this.toplamVarliklar = toplamVarliklar;        this.dagitilmayanKarlar = dagitilmayanKarlar;        this.faizVeVergiOncesiKar = faizVeVergiOncesiKar;        this.ozkaynak = ozkaynak;        this.borclar = borclar;        this.satislar = satislar;    }}